import React, {useState, useEffect} from 'react';
import axios from 'axios';

import './App.css';
function FetchUser() {
    const [posts, setPosts] = useState([])
    const [year, setYear] = useState(2014)
    const [launch, setLaunch] = useState(true)
    const [landing, setLanding] = useState(true)
    const apiUrl = 'https://api.spaceXdata.com/v3/launches?limit=100';
  if(year && launch && landing){
    let apiUrl= `${apiUrl}&launch_success=${launch}&land_success=${landing}&launch_year=${year}`;
  }
  if(launch){
    let apiUrl = `${apiUrl}&launch_success=${launch}`;
  }
  if(landing){
    let apiUrl = `${apiUrl}&land_success=${landing}`;
  }
    useEffect(() => {
        axios.get(apiUrl)
        .then(res => {
            console.log(res)
            setPosts(res.data)
        })
        .catch(err =>{
             console.log(err)
        })
    },[posts,year,launch,landing])

   return (
    <section className="wrapper">
    <h1 className="heading">SpaceX Launch Programs</h1>
    <section className="left-section"> 
    <h2 className="filter">Filters</h2>  
    <h2 className="launch-year">Launch Year</h2>
    <hr size="3" color="#ccc" />          
    <ul>
        {
            posts.map(item => {                 
                return ( <li className="li-list"  onClick={e => setYear(e.target.value)} value={year} key={item.flight_number}>{item.launch_year}</li> ) 
            })
        }
    </ul>  
    <h2 className="launch-year">Successful Launch</h2>
    <hr size="3" color="#ccc" />   
    <div className="li-list" onClick={e => setLaunch(e.target.value)}>true</div> 
    <div className="li-list">false</div> 

    <h2 className="launch-year">Successful Landing</h2>
    <hr size="3" color="#ccc" />   
    <div className="li-list" onClick={e => setLanding(e.target.value)}>true</div> 
    <div className="li-list">false</div>
   
</section>
<section className="right-section">
 {
posts.map(item => {
   return (       
   <div className="right-div">
        <img className="spaceXimg" alt={item.mission_name} src={item.links.mission_patch_small}/>
        <div className="img-content">
            <h3>{item.mission_name}</h3>
            <h3>Mission ids: {item.mission_id}</h3>
            <h3>Launch Year: {item.launch_year}</h3>
            <h3>successful launch : {item.launch_success}</h3>
            <h3>successful landing :{item.is_tentative}</h3>
        </div>
    </div> 
   )
})
}
</section>
</section>
   )    
}

export default FetchUser
