function DisplayUser({data}) {
    function FilterData(query){
        const fetchUrl = `https://api.spaceXdata.com/v3/launches?limit=100&launch_success=true&land_success=true&launch_year=${query}`;
  
            axios.get(fetchUrl)
            .then(res => {
                console.log(res)
                setPosts(res.data)
            })
            .catch(err =>{
                 console.log(err)
            })
    }
    return ( 
        <section className="wrapper">
        <h1 className="heading">SpaceX Launch Programs</h1>
        <section className="left-section"> 
        <h2 className="filter">Filters</h2>  
        <h2 className="launch-year">Launch Year</h2>
        <hr size="3" color="#ccc" />          
        <ul>
            {
                data.map(item => {
                  
                    return ( <li className="li-list" key={item.flight_number} onClick={(e)=> FilterData(e.target.value)}>{item.launch_year}</li> ) })
                 
            }
        </ul>  
        <h2 className="launch-year">Successful Launch</h2>
        <hr size="3" color="#ccc" />   
        <div className="li-list">true</div> 
        <div class="li-list">false</div> 
      
        <h2 className="launch-year">Successful Landing</h2>
        <hr size="3" color="#ccc" />   
        <div className="li-list">true</div> 
        <div class="li-list">false</div>
           
        </section>
        <section className="right-section">
         {
        data.map(item => {
           return (       
           <div className="right-div">
                <img className="spaceXimg" alt={item.mission_name} src={item.links.mission_patch_small}/>
                <div className="img-content">
                    <h3>{item.mission_name}</h3>
                    <h3>Mission ids: {item.mission_id}</h3>
                    <h3>Launch Year: {item.launch_year}</h3>
                    <h3>successful launch : {item.launch_success}</h3>
                    <h3>successful landing :{item.is_tentative}</h3>
                </div>
            </div> 
           )
        })
       }
        </section>
        </section>
        
    )
}

export default DisplayUser;
